Scripts.OnLoad:
self.IsOn = false;
self.Label:SetText(NARCI_BORDER_THEME_BRIGHT);
if not self:GetParent().buttons then
    self:GetParent().buttons = {};  
end
tinsert(self:GetParent().buttons, self);

self.IsOn = false;
self.Label:SetText(NARCI_LAYOUT_1)
if not self:GetParent().buttons then
    self:GetParent().buttons = {};  
end
tinsert(self:GetParent().buttons, self)
