Scripts.OnShow:
local width = Narci_VignetteLeft:GetWidth();
local height = Narci_VignetteLeft:GetHeight();
local alpha = Narci_VignetteLeft:GetAlpha();
self.VignetteRightSmall:SetSize(width, height);
self.VignetteRightSmall:SetAlpha(alpha)

Scripts.OnMouseUp:
if ( button == "RightButton" and self.panning ) then
	self.panning = false;
elseif ( self.mouseDown ) then
	self.onMouseUpFunc(self, button);
end
self.GuideLineCenter:Hide();

Scripts.OnMouseDown:
if ( button == "RightButton" and not self.mouseDown ) then
	NarciModel_StartPanning(self);
else
	NarciModel_OnMouseDown(self, button);
end
if self.GuideFrame:IsVisible() then
	self.GuideLineCenter:Show();
end

Scripts.OnHide:
if ( self.panning ) then
	self.panning = false;
end
self.mouseDown = false;
Narci_CharacterModelSettings:Hide();
self.ChromaKey:Hide();
self.ChromaKey:SetAlpha(0);

Scripts.OnLoad:
self.animID = self:GetID() or 1;
if not self:GetParent().buttons then
	self:GetParent().buttons = {};
end
tinsert(self:GetParent().buttons, self);

Scripts.OnClick:
Narci_CharacterModelFrame:SetAnimation(self:GetID(), 1)
local buttons = self:GetParent().buttons;
for i=1, #buttons do
	buttons[i].Highlight:Hide();
	buttons[i].IsOn = false;
end
Narci_Model_IdleButton.IsOn = false;
Narci_Model_IdleButton.Highlight:Hide();
self.IsOn = true;
self.Highlight:Show();

Scripts.OnHide:
local _, _, dirX, dirY, dirZ = Narci_CharacterModelFrame:GetLight();
local Format_Digit = "%.2f";
--print("OFF")
--print("dirX: "..string.format(Format_Digit, dirX).." dirY: "..string.format(Format_Digit, dirY).." dirZ: "..string.format(Format_Digit, dirZ))

Scripts.OnLoad:
self.r, self.g, self.b = 0, 177/255, 64/255;
self.Color:SetColorTexture(self.r, self.g, self.b);
self.Border:SetTexCoord(0.8359375, 0.921875, 0, 0.171875)

Scripts.OnClick:
local frame = Narci_GroundShadowFrame;
frame:ClearAllPoints();
frame:SetPoint("BOTTOM", UIParent, "BOTTOM", 0 ,0);
frame:SetPoint("CENTER", Narci_CharacterModelFrame.GuideLineCenter, "BOTTOM", 0 ,-100);
frame:SetUserPlaced(false);
Narci_ModelShadow_SizeSlider:SetValue(1);
Narci_ModelShadow_AlphaSlider:SetValue(1);

self.IsOn = not self.IsOn;
if self.IsOn then
	self:LockHighlight();
	self.Label:SetTextColor(1, 1, 1);
	Narci_GroundShadowFrame:Show();	
else
	self:UnlockHighlight();
	self.Label:SetTextColor(0.6, 0.6, 0.6);
	Narci_GroundShadowFrame:Hide();
end

Scripts.OnShow:
self.IsOn = false;
self:UnlockHighlight();
self.Label:SetTextColor(0.6, 0.6, 0.6)
if NarcissusDB.DefaultLayout == 3 then
	self:Click();
end

Scripts.OnLeave:
if not self:HasFocus() then
	self.Highlight:Hide();
end

Scripts.OnEditFocusLost:
self.Highlight:Hide();
if self:GetNumber() > 1447000 then
	self:SetNumber(1447);
end
if self:GetParent().Pause.IsOn then
	Narci_CharacterModelFrame:FreezeAnimation(math.min(self:GetNumber(), 1447), 0, 1);
	Narci_Model_AnimationFrameSlider:SetValue(1);
else
	Narci_CharacterModelFrame:SetAnimation(math.min(self:GetNumber(), 1447), 1)
end
Narci_Model_IdleButton.IsOn = false;
Narci_Model_IdleButton.Highlight:Hide();

Scripts.OnClick:
Narci_AnimationIDFrame_EditBox:ClearFocus();
local id = Narci_AnimationIDFrame_EditBox:GetNumber();
Narci_CharacterModelFrame:FreezeAnimation(id, 0, 1)
self.IsOn = true;
self:GetParent().Play.IsOn = false;
self:GetParent().Play.Highlight:Hide();
self:GetParent().AnimFrameSlider:Show();
Narci_Model_IdleButton.IsOn = false;
Narci_Model_IdleButton.Highlight:Hide();

self.IsOn = true;
self:GetParent().Pause.IsOn = false;
self:GetParent().Pause.Highlight:Hide();
self:GetParent().AnimFrameSlider:Hide();
Narci_AnimationIDFrame_EditBox:ClearFocus();
local id = Narci_AnimationIDFrame_EditBox:GetNumber();
Narci_CharacterModelFrame:SetAnimation(id, 1)
Narci_Model_IdleButton.IsOn = false;
Narci_Model_IdleButton.Highlight:Hide();
Narci_CharacterModelFrame:SetPaused(false)
